{"filter":false,"title":"hutagososu.rb","tooltip":"/hutagososu.rb","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":20,"column":32},"action":"insert","lines":["def sieve(a,i,f)","  b = a.select{|x| x<=Math.sqrt(i)}.map{|x| i%x != 0}.all?","  if (b) then","    a = a + [i]","    if a.select{|x| x==(i-f)}.length>0 then","      return a","    end","  end","  return sieve(a,i+1,f)","end","","def twin(f,c)","  return (0..(c-1)).to_a.inject([[2],[]]){|a,_|","    b = sieve(a[0],a[0].last+1,f)","    [b,a[1]+[b.last]]","  }[1].map{|x| [x-f,x]}","end","","print(twin(2,10)) # 双子素数を10組表示","print(twin(4,10)) # いとこ素数を10組表示","print(twin(6,10)) # セクシー素数を10組表示"],"id":1}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":20,"column":32},"end":{"row":20,"column":32},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1636896917894,"hash":"c946fe32100e2a5bb1a3337d2685ebe70dee48b8"}